import React, { Component } from "react";
import Carousel from "react-spring-3d-carousel";
import { v4 as uuidv4 } from "uuid";
import { config } from "react-spring";

export default class Example extends Component {
  state = {
    goToSlide: 0,
    offsetRadius: 2,
    showNavigation: true,
    config: config.gentle,
    isMobile: window.innerWidth <= 768 // Menambahkan state untuk menandai mode mobile
  };

  slides = [
    {
      key: uuidv4(),
      content: <img src="https://picsum.photos/800/801/?random" alt="1" style={{ width: "700px", height: "300px" }} />
    },
    {
      key: uuidv4(),
      content: <img src="https://picsum.photos/800/802/?random" alt="2" style={{ width: "700px", height: "300px" }} />
    },
    {
      key: uuidv4(),
      content: <img src="https://picsum.photos/600/803/?random" alt="3" style={{ width: "700px", height: "300px" }} />
    }
  ].map((slide, index) => {
    return { ...slide, onClick: () => this.setState({ goToSlide: index }) };
  });

  // Fungsi untuk menangani perubahan ukuran layar
  handleResize = () => {
    this.setState({ isMobile: window.innerWidth <= 768 });
  };

  componentDidMount() {
    this.handleResize();
    window.addEventListener("resize", this.handleResize);
  }

  componentWillUnmount() {
    window.removeEventListener("resize", this.handleResize);
  }

  render() {
    return (
      <div style={{ width: "80%", height: "500px", margin: "0 auto" }}>
        {/* Tampilkan Carousel hanya jika bukan mode mobile */}
        {!this.state.isMobile && (
          <Carousel
            slides={this.slides}
            goToSlide={this.state.goToSlide}
            offsetRadius={this.state.offsetRadius}
            showNavigation={this.state.showNavigation}
            animationConfig={this.state.config}
          />
        )}

        {/* Pesan untuk mode mobile */}
        {this.state.isMobile && (
          <div style={{ textAlign: "center", padding: "20px" }}>
            <p>
              Tampilan Carousel tidak didukung di mode mobile. Silakan coba di
              perangkat dengan layar lebih besar.
            </p>
          </div>
        )}

        {/* Placeholder atau konten lain untuk mode mobile */}
        {this.state.isMobile && (
          <div
            style={{
              margin: "0 auto",
              marginTop: "2rem",
              width: "50%",
              display: "flex",
              justifyContent: "space-around"
            }}
          >
            {/* Konten untuk mode mobile bisa diletakkan di sini */}
          </div>
        )}
      </div>
    );
  }
}
