import React, { Component } from "react";
import Carousel from "react-spring-3d-carousel";
import { v4 as uuidv4 } from "uuid";
import { config } from "react-spring";

export default class Example extends Component {
  state = {
    goToSlide: 0,
    offsetRadius: 2,
    showNavigation: false,
    config: config.gentle,
    isMobile: window.innerWidth <= 768
  };

  slides = [
    {
      key: uuidv4(),
      content: <img src="https://ichijougr.files.wordpress.com/2017/04/wp-1492955740125.jpeg" alt="1" style={{ height: "300px" }} />
    },
    {
      key: uuidv4(),
      content: <img src="https://picsum.photos/800/802/?random" alt="2" style={{ height: "300px" }} />
    },
    {
      key: uuidv4(),
      content: <img src="https://picsum.photos/600/803/?random" alt="3" style={{ height: "300px" }} />
    }
  ].map((slide, index) => {
    return { ...slide, onClick: () => this.setState({ goToSlide: index }) };
  });

  handleResize = () => {
    this.setState({ isMobile: window.innerWidth <= 768 });
  };

  componentDidMount() {
    this.handleResize();
    window.addEventListener("resize", this.handleResize);

    // Mengatur interval untuk mengganti slide setiap beberapa detik
    this.interval = setInterval(() => {
      this.setState((prevState) => ({
        goToSlide: (prevState.goToSlide + 1) % this.slides.length
      }));
    }, 5000); // Ganti slide setiap 5 detik (5000 milidetik)
  }

  componentWillUnmount() {
    window.removeEventListener("resize", this.handleResize);

    // Membersihkan interval saat komponen di-unmount
    clearInterval(this.interval);
  }

  render() {
    return (
      <div style={{ width: "80%", height: "500px", margin: "0 auto", padding: "100px", position: "relative" }}>
        {/* Tombol sebelumnya (prevButton) */}
        <button
          style={{ position: "absolute", left: "10px", top: "50%", transform: "translateY(-50%)", background: "none", border: "none", fontSize: "24px" }}
          onClick={() => this.setState({ goToSlide: this.state.goToSlide - 1 })}
        >
          {'<'}
        </button>

        {/* Carousel */}
        <Carousel
          slides={this.slides}
          goToSlide={this.state.goToSlide}
          offsetRadius={this.state.offsetRadius}
          showNavigation={this.state.showNavigation}
          animationConfig={this.state.config}
        />

        {/* Tombol selanjutnya (nextButton) */}
        <button
          style={{ position: "absolute", right: "10px", top: "50%", transform: "translateY(-50%)", background: "none", border: "none", fontSize: "24px" }}
          onClick={() => this.setState({ goToSlide: this.state.goToSlide + 1 })}
        >
          {'>'}
        </button>

        {this.state.isMobile && (
          <div style={{ textAlign: "center", padding: "20px" }}>
            <p>
              Tampilan Carousel tidak didukung di mode mobile. Silakan coba di
              perangkat dengan layar lebih besar.
            </p>
          </div>
        )}

        {this.state.isMobile && (
          <div
            style={{
              margin: "0 auto",
              marginTop: "2rem",
              width: "50%",
              display: "flex",
              justifyContent: "space-around"
            }}
          >
            {/* Konten untuk mode mobile bisa diletakkan di sini */}
          </div>
        )}
      </div>
    );
  }
}
